import axios from 'axios';


export const [userid,setUserid] = useState(0);

function Menu() {


    const [userid,setUserid] = useState(0);

    let menu;

    const [datas,setDatas] = useState([]);


    const getUsers = async () => {
            const response = await axios.get('/ep');
            setDatas(response.data);
        }

        useEffect(() => {
            getUsers();
        }, []);

        const options = datas.map((user, index) => {
            return(

                {value: user.id,label: user.name}

            );
        });

    const handleSubmit = ()=>{

    }

    const handleChange = (e)=>{
        setUserid()

    }





    if(userid === 0){
        menu = (
        <div>    
        <h1>select user</h1>

        <form onSubmit={handleSubmit()}><Select options={options} onChange={handleChange()} /><input type="submit" /></form>


        </div>

        );



    }else{
        menu = (
        <ul>
            <li>
                <Link to='/list'>List</Link>
            </li>
            <li>
                <Link to='/new'>New</Link>
            </li>
        </ul>);
    }



    return (
        <div>

        {menu}





        </div>

    );

}


export default Menu;
